static void filterScanline(unsigned char* out, const unsigned char* scanline, const unsigned char* prevline,
						   size_t length, size_t bytewidth, unsigned char filterType)
{
  size_t i;
  switch(filterType)
  {
	case 0: /*None*/
	  for(i = 0; i != length; ++i) out[i] = scanline[i];
	  break;
	case 1: /*Sub*/
	  for(i = 0; i != bytewidth; ++i) out[i] = scanline[i];
	  for(i = bytewidth; i < length; ++i) out[i] = scanline[i] - scanline[i - bytewidth];
	  break;
	case 2: /*Up*/
	  if(prevline)
	  {
		for(i = 0; i != length; ++i) out[i] = scanline[i] - prevline[i];
	  }
	  else
	  {
		for(i = 0; i != length; ++i) out[i] = scanline[i];
	  }
	  break;
	case 3: /*Average*/
	  if(prevline)
	  {
		for(i = 0; i != bytewidth; ++i) out[i] = scanline[i] - prevline[i] / 2;
		for(i = bytewidth; i < length; ++i) out[i] = scanline[i] - ((scanline[i - bytewidth] + prevline[i]) / 2);
	  }
	  else
	  {
		for(i = 0; i != bytewidth; ++i) out[i] = scanline[i];
		for(i = bytewidth; i < length; ++i) out[i] = scanline[i] - scanline[i - bytewidth] / 2;
	  }
	  break;
	case 4: /*Paeth*/
	  if(prevline)
	  {
		/*paethPredictor(0, prevline[i], 0) is always prevline[i]*/
		for(i = 0; i != bytewidth; ++i) out[i] = (scanline[i] - prevline[i]);
		for(i = bytewidth; i < length; ++i)
		{
		  out[i] = (scanline[i] - paethPredictor(scanline[i - bytewidth], prevline[i], prevline[i - bytewidth]));
		}
	  }
	  else
	  {
		for(i = 0; i != bytewidth; ++i) out[i] = scanline[i];
		/*paethPredictor(scanline[i - bytewidth], 0, 0) is always scanline[i - bytewidth]*/
		for(i = bytewidth; i < length; ++i) out[i] = (scanline[i] - scanline[i - bytewidth]);
	  }
	  break;
	default: return; /*unexisting filter type given*/
  }
}

/* log2 approximation. A slight bit faster than std::log. */
static float flog2(float f)
{
  float result = 0;
  while(f > 32) { result += 4; f /= 16; }
  while(f > 2) { ++result; f /= 2; }
  return result + 1.442695f * (f * f * f / 3 - 3 * f * f / 2 + 3 * f - 1.83333f);
}

static unsigned filter(unsigned char* out, const unsigned char* in, unsigned w, unsigned h,
					   const LodePNGColorMode* info, const LodePNGEncoderSettings* settings)
{
  /*
  For PNG filter method 0
  out must be a buffer with as size: h + (w * h * bpp + 7) / 8, because there are
  the scanlines with 1 extra byte per scanline
  */

  unsigned bpp = lodepng_get_bpp(info);
  /*the width of a scanline in bytes, not including the filter type*/
  size_t linebytes = (w * bpp + 7) / 8;
  /*bytewidth is used for filtering, is 1 when bpp < 8, number of bytes per pixel otherwise*/
  size_t bytewidth = (bpp + 7) / 8;
  const unsigned char* prevline = 0;
  unsigned x, y;
  unsigned error = 0;
  LodePNGFilterStrategy strategy = settings->filter_strategy;

  /*
  There is a heuristic called the minimum sum of absolute differences heuristic, suggested by the PNG standard:
   *  If the image type is Palette, or the bit depth is smaller than 8, then do not filter the image (i.e.
	  use fixed filtering, with the filter None).
   * (The other case) If the image type is Grayscale or RGB (with or without Alpha), and the bit depth is
	 not smaller than 8, then use adaptive filtering heuristic as follows: independently for each row, apply
	 all five filters and select the filter that produces the smallest sum of absolute values per row.
  This heuristic is used if filter strategy is LFS_MINSUM and filter_palette_zero is true.

  If filter_palette_zero is true and filter_strategy is not LFS_MINSUM, the above heuristic is followed,
  but for "the other case", whatever strategy filter_strategy is set to instead of the minimum sum
  heuristic is used.
  */
  if(settings->filter_palette_zero &&
	 (info->colortype == LCT_PALETTE || info->bitdepth < 8)) strategy = LFS_ZERO;

  if(bpp == 0) return 31; /*error: invalid color type*/

  if(strategy == LFS_ZERO)
  {
	for(y = 0; y != h; ++y)
	{
	  size_t outindex = (1 + linebytes) * y; /*the extra filterbyte added to each row*/
	  size_t inindex = linebytes * y;
	  out[outindex] = 0; /*filter type byte*/
	  filterScanline(&out[outindex + 1], &in[inindex], prevline, linebytes, bytewidth, 0);
	  prevline = &in[inindex];
	}
  }
  else if(strategy == LFS_MINSUM)
  {
	/*adaptive filtering*/
	size_t sum[5];
	ucvector attempt[5]; /*five filtering attempts, one for each filter type*/
	size_t smallest = 0;
	unsigned char type, bestType = 0;

	for(type = 0; type != 5; ++type)
	{
	  ucvector_init(&attempt[type]);
	  if(!ucvector_resize(&attempt[type], linebytes)) return 83; /*alloc fail*/
	}

	if(!error)
	{
	  for(y = 0; y != h; ++y)
	  {
		/*try the 5 filter types*/
		for(type = 0; type != 5; ++type)
		{
		  filterScanline(attempt[type].data, &in[y * linebytes], prevline, linebytes, bytewidth, type);

		  /*calculate the sum of the result*/
		  sum[type] = 0;
		  if(type == 0)
		  {
			for(x = 0; x != linebytes; ++x) sum[type] += (unsigned char)(attempt[type].data[x]);
		  }
		  else
		  {
			for(x = 0; x != linebytes; ++x)
			{
			  /*For differences, each byte should be treated as signed, values above 127 are negative
			  (converted to signed char). Filtertype 0 isn't a difference though, so use unsigned there.
			  This means filtertype 0 is almost never chosen, but that is justified.*/
			  unsigned char s = attempt[type].data[x];
			  sum[type] += s < 128 ? s : (255U - s);
			}
		  }

		  /*check if this is smallest sum (or if type == 0 it's the first case so always store the values)*/
		  if(type == 0 || sum[type] < smallest)
		  {
			bestType = type;
			smallest = sum[type];
		  }
		}

		prevline = &in[y * linebytes];

		/*now fill the out values*/
		out[y * (linebytes + 1)] = bestType; /*the first byte of a scanline will be the filter type*/
		for(x = 0; x != linebytes; ++x) out[y * (linebytes + 1) + 1 + x] = attempt[bestType].data[x];
	  }
	}

	for(type = 0; type != 5; ++type) ucvector_cleanup(&attempt[type]);
  }
  else if(strategy == LFS_ENTROPY)
  {
	float sum[5];
	ucvector attempt[5]; /*five filtering attempts, one for each filter type*/
	float smallest = 0;
	unsigned type, bestType = 0;
	unsigned count[256];

	for(type = 0; type != 5; ++type)
	{
	  ucvector_init(&attempt[type]);
	  if(!ucvector_resize(&attempt[type], linebytes)) return 83; /*alloc fail*/
	}

	for(y = 0; y != h; ++y)
	{
	  /*try the 5 filter types*/
	  for(type = 0; type != 5; ++type)
	  {
		filterScanline(attempt[type].data, &in[y * linebytes], prevline, linebytes, bytewidth, type);
		for(x = 0; x != 256; ++x) count[x] = 0;
		for(x = 0; x != linebytes; ++x) ++count[attempt[type].data[x]];
		++count[type]; /*the filter type itself is part of the scanline*/
		sum[type] = 0;
		for(x = 0; x != 256; ++x)
		{
		  float p = count[x] / (float)(linebytes + 1);
		  sum[type] += count[x] == 0 ? 0 : flog2(1 / p) * p;
		}
		/*check if this is smallest sum (or if type == 0 it's the first case so always store the values)*/
		if(type == 0 || sum[type] < smallest)
		{
		  bestType = type;
		  smallest = sum[type];
		}
	  }

	  prevline = &in[y * linebytes];

	  /*now fill the out values*/
	  out[y * (linebytes + 1)] = bestType; /*the first byte of a scanline will be the filter type*/
	  for(x = 0; x != linebytes; ++x) out[y * (linebytes + 1) + 1 + x] = attempt[bestType].data[x];
	}

	for(type = 0; type != 5; ++type) ucvector_cleanup(&attempt[type]);
  }
  else if(strategy == LFS_PREDEFINED)
  {
	for(y = 0; y != h; ++y)
	{
	  size_t outindex = (1 + linebytes) * y; /*the extra filterbyte added to each row*/
	  size_t inindex = linebytes * y;
	  unsigned char type = settings->predefined_filters[y];
	  out[outindex] = type; /*filter type byte*/
	  filterScanline(&out[outindex + 1], &in[inindex], prevline, linebytes, bytewidth, type);
	  prevline = &in[inindex];
	}
  }
  else if(strategy == LFS_BRUTE_FORCE)
  {
	/*brute force filter chooser.
	deflate the scanline after every filter attempt to see which one deflates best.
	This is very slow and gives only slightly smaller, sometimes even larger, result*/
	size_t size[5];
	ucvector attempt[5]; /*five filtering attempts, one for each filter type*/
	size_t smallest = 0;
	unsigned type = 0, bestType = 0;
	unsigned char* dummy;
	LodePNGCompressSettings zlibsettings = settings->zlibsettings;
	/*use fixed tree on the attempts so that the tree is not adapted to the filtertype on purpose,
	to simulate the true case where the tree is the same for the whole image. Sometimes it gives
	better result with dynamic tree anyway. Using the fixed tree sometimes gives worse, but in rare
	cases better compression. It does make this a bit less slow, so it's worth doing this.*/
	zlibsettings.btype = 1;
	/*a custom encoder likely doesn't read the btype setting and is optimized for complete PNG
	images only, so disable it*/
	zlibsettings.custom_zlib = 0;
	zlibsettings.custom_deflate = 0;
	for(type = 0; type != 5; ++type)
	{
	  ucvector_init(&attempt[type]);
	  ucvector_resize(&attempt[type], linebytes); /*todo: give error if resize failed*/
	}
	for(y = 0; y != h; ++y) /*try the 5 filter types*/
	{
	  for(type = 0; type != 5; ++type)
	  {
		unsigned testsize = attempt[type].size;
		/*if(testsize > 8) testsize /= 8;*/ /*it already works good enough by testing a part of the row*/

		filterScanline(attempt[type].data, &in[y * linebytes], prevline, linebytes, bytewidth, type);
		size[type] = 0;
		dummy = 0;
		zlib_compress(&dummy, &size[type], attempt[type].data, testsize, &zlibsettings);
		lodepng_free(dummy);
		/*check if this is smallest size (or if type == 0 it's the first case so always store the values)*/
		if(type == 0 || size[type] < smallest)
		{
		  bestType = type;
		  smallest = size[type];
		}
	  }
	  prevline = &in[y * linebytes];
	  out[y * (linebytes + 1)] = bestType; /*the first byte of a scanline will be the filter type*/
	  for(x = 0; x != linebytes; ++x) out[y * (linebytes + 1) + 1 + x] = attempt[bestType].data[x];
	}
	for(type = 0; type != 5; ++type) ucvector_cleanup(&attempt[type]);
  }
  else return 88; /* unknown filter strategy */

  return error;
}

static void addPaddingBits(unsigned char* out, const unsigned char* in,
						   size_t olinebits, size_t ilinebits, unsigned h)
{
  /*The opposite of the removePaddingBits function
  olinebits must be >= ilinebits*/
  unsigned y;
  size_t diff = olinebits - ilinebits;
  size_t obp = 0, ibp = 0; /*bit pointers*/
  for(y = 0; y != h; ++y)
  {
	size_t x;
	for(x = 0; x < ilinebits; ++x)
	{
	  unsigned char bit = readBitFromReversedStream(&ibp, in);
	  setBitOfReversedStream(&obp, out, bit);
	}
	/*obp += diff; --> no, fill in some value in the padding bits too, to avoid
	"Use of uninitialised value of size ###" warning from valgrind*/
	for(x = 0; x != diff; ++x) setBitOfReversedStream(&obp, out, 0);
  }
}

/*
in: non-interlaced image with size w*h
out: the same pixels, but re-ordered according to PNG's Adam7 interlacing, with
 no padding bits between scanlines, but between reduced images so that each
 reduced image starts at a byte.
bpp: bits per pixel
there are no padding bits, not between scanlines, not between reduced images
in has the following size in bits: w * h * bpp.
out is possibly bigger due to padding bits between reduced images
NOTE: comments about padding bits are only relevant if bpp < 8
*/
static void Adam7_interlace(unsigned char* out, const unsigned char* in, unsigned w, unsigned h, unsigned bpp)
{
  unsigned passw[7], passh[7];
  size_t filter_passstart[8], padded_passstart[8], passstart[8];
  unsigned i;

  Adam7_getpassvalues(passw, passh, filter_passstart, padded_passstart, passstart, w, h, bpp);

  if(bpp >= 8)
  {
	for(i = 0; i != 7; ++i)
	{
	  unsigned x, y, b;
	  size_t bytewidth = bpp / 8;
	  for(y = 0; y < passh[i]; ++y)
	  for(x = 0; x < passw[i]; ++x)
	  {
		size_t pixelinstart = ((ADAM7_IY[i] + y * ADAM7_DY[i]) * w + ADAM7_IX[i] + x * ADAM7_DX[i]) * bytewidth;
		size_t pixeloutstart = passstart[i] + (y * passw[i] + x) * bytewidth;
		for(b = 0; b < bytewidth; ++b)
		{
		  out[pixeloutstart + b] = in[pixelinstart + b];
		}
	  }
	}
  }
  else /*bpp < 8: Adam7 with pixels < 8 bit is a bit trickier: with bit pointers*/
  {
	for(i = 0; i != 7; ++i)
	{
	  unsigned x, y, b;
	  unsigned ilinebits = bpp * passw[i];
	  unsigned olinebits = bpp * w;
	  size_t obp, ibp; /*bit pointers (for out and in buffer)*/
	  for(y = 0; y < passh[i]; ++y)
	  for(x = 0; x < passw[i]; ++x)
	  {
		ibp = (ADAM7_IY[i] + y * ADAM7_DY[i]) * olinebits + (ADAM7_IX[i] + x * ADAM7_DX[i]) * bpp;
		obp = (8 * passstart[i]) + (y * ilinebits + x * bpp);
		for(b = 0; b < bpp; ++b)
		{
		  unsigned char bit = readBitFromReversedStream(&ibp, in);
		  setBitOfReversedStream(&obp, out, bit);
		}
	  }
	}
  }
}

/*out must be buffer big enough to contain uncompressed IDAT chunk data, and in must contain the full image.
return value is error**/
static unsigned preProcessScanlines(unsigned char** out, size_t* outsize, const unsigned char* in,
									unsigned w, unsigned h,
									const LodePNGInfo* info_png, const LodePNGEncoderSettings* settings)
{
  /*
  This function converts the pure 2D image with the PNG's colortype, into filtered-padded-interlaced data. Steps:
  *) if no Adam7: 1) add padding bits (= posible extra bits per scanline if bpp < 8) 2) filter
  *) if adam7: 1) Adam7_interlace 2) 7x add padding bits 3) 7x filter
  */
  unsigned bpp = lodepng_get_bpp(&info_png->color);
  unsigned error = 0;

  if(info_png->interlace_method == 0)
  {
	*outsize = h + (h * ((w * bpp + 7) / 8)); /*image size plus an extra byte per scanline + possible padding bits*/
	*out = (unsigned char*)lodepng_malloc(*outsize);
	if(!(*out) && (*outsize)) error = 83; /*alloc fail*/

	if(!error)
	{
	  /*non multiple of 8 bits per scanline, padding bits needed per scanline*/
	  if(bpp < 8 && w * bpp != ((w * bpp + 7) / 8) * 8)
	  {
		unsigned char* padded = (unsigned char*)lodepng_malloc(h * ((w * bpp + 7) / 8));
		if(!padded) error = 83; /*alloc fail*/
		if(!error)
		{
		  addPaddingBits(padded, in, ((w * bpp + 7) / 8) * 8, w * bpp, h);
		  error = filter(*out, padded, w, h, &info_png->color, settings);
		}
		lodepng_free(padded);
	  }
	  else
	  {
		/*we can immediatly filter into the out buffer, no other steps needed*/
		error = filter(*out, in, w, h, &info_png->color, settings);
	  }
	}
  }
  else /*interlace_method is 1 (Adam7)*/
  {
	unsigned passw[7], passh[7];
	size_t filter_passstart[8], padded_passstart[8], passstart[8];
	unsigned char* adam7;

	Adam7_getpassvalues(passw, passh, filter_passstart, padded_passstart, passstart, w, h, bpp);

	*outsize = filter_passstart[7]; /*image size plus an extra byte per scanline + possible padding bits*/
	*out = (unsigned char*)lodepng_malloc(*outsize);
	if(!(*out)) error = 83; /*alloc fail*/

	adam7 = (unsigned char*)lodepng_malloc(passstart[7]);
	if(!adam7 && passstart[7]) error = 83; /*alloc fail*/

	if(!error)
	{
	  unsigned i;

	  Adam7_interlace(adam7, in, w, h, bpp);
	  for(i = 0; i != 7; ++i)
	  {
		if(bpp < 8)
		{
		  unsigned char* padded = (unsigned char*)lodepng_malloc(padded_passstart[i + 1] - padded_passstart[i]);
		  if(!padded) ERROR_BREAK(83); /*alloc fail*/
		  addPaddingBits(padded, &adam7[passstart[i]],
						 ((passw[i] * bpp + 7) / 8) * 8, passw[i] * bpp, passh[i]);
		  error = filter(&(*out)[filter_passstart[i]], padded,
						 passw[i], passh[i], &info_png->color, settings);
		  lodepng_free(padded);
		}
		else
		{
		  error = filter(&(*out)[filter_passstart[i]], &adam7[padded_passstart[i]],
						 passw[i], passh[i], &info_png->color, settings);
		}

		if(error) break;
	  }
	}

	lodepng_free(adam7);
  }

  return error;
}

/*
palette must have 4 * palettesize bytes allocated, and given in format RGBARGBARGBARGBA...
returns 0 if the palette is opaque,
returns 1 if the palette has a single color with alpha 0 ==> color key
returns 2 if the palette is semi-translucent.
*/
static unsigned getPaletteTranslucency(const unsigned char* palette, size_t palettesize)
{
  size_t i;
  unsigned key = 0;
  unsigned r = 0, g = 0, b = 0; /*the value of the color with alpha 0, so long as color keying is possible*/
  for(i = 0; i != palettesize; ++i)
  {
	if(!key && palette[4 * i + 3] == 0)
	{
	  r = palette[4 * i + 0]; g = palette[4 * i + 1]; b = palette[4 * i + 2];
	  key = 1;
	  i = (size_t)(-1); /*restart from beginning, to detect earlier opaque colors with key's value*/
	}
	else if(palette[4 * i + 3] != 255) return 2;
	/*when key, no opaque RGB may have key's RGB*/
	else if(key && r == palette[i * 4 + 0] && g == palette[i * 4 + 1] && b == palette[i * 4 + 2]) return 2;
  }
  return key;
}

#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
static unsigned addUnknownChunks(ucvector* out, unsigned char* data, size_t datasize)
{
  unsigned char* inchunk = data;
  while((size_t)(inchunk - data) < datasize)
  {
	CERROR_TRY_RETURN(lodepng_chunk_append(&out->data, &out->size, inchunk));
	out->allocsize = out->size; /*fix the allocsize again*/
	inchunk = lodepng_chunk_next(inchunk);
  }
  return 0;
}
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/

unsigned lodepng_encode(unsigned char** out, size_t* outsize,
						const unsigned char* image, unsigned w, unsigned h,
						LodePNGState* state)
{
  LodePNGInfo info;
  ucvector outv;
  unsigned char* data = 0; /*uncompressed version of the IDAT chunk data*/
  size_t datasize = 0;

  /*provide some proper output values if error will happen*/
  *out = 0;
  *outsize = 0;
  state->error = 0;

  lodepng_info_init(&info);
  lodepng_info_copy(&info, &state->info_png);

  if((info.color.colortype == LCT_PALETTE || state->encoder.force_palette)
	  && (info.color.palettesize == 0 || info.color.palettesize > 256))
  {
	state->error = 68; /*invalid palette size, it is only allowed to be 1-256*/
	return state->error;
  }

  if(state->encoder.auto_convert)
  {
	state->error = lodepng_auto_choose_color(&info.color, image, w, h, &state->info_raw);
  }
  if(state->error) return state->error;

  if(state->encoder.zlibsettings.btype > 2)
  {
	CERROR_RETURN_ERROR(state->error, 61); /*error: unexisting btype*/
  }
  if(state->info_png.interlace_method > 1)
  {
	CERROR_RETURN_ERROR(state->error, 71); /*error: unexisting interlace mode*/
  }

  state->error = checkColorValidity(info.color.colortype, info.color.bitdepth);
  if(state->error) return state->error; /*error: unexisting color type given*/
  state->error = checkColorValidity(state->info_raw.colortype, state->info_raw.bitdepth);
  if(state->error) return state->error; /*error: unexisting color type given*/

  if(!lodepng_color_mode_equal(&state->info_raw, &info.color))
  {
	unsigned char* converted;
	size_t size = (w * h * lodepng_get_bpp(&info.color) + 7) / 8;

	converted = (unsigned char*)lodepng_malloc(size);
	if(!converted && size) state->error = 83; /*alloc fail*/
	if(!state->error)
	{
	  state->error = lodepng_convert(converted, image, &info.color, &state->info_raw, w, h);
	}
	if(!state->error) preProcessScanlines(&data, &datasize, converted, w, h, &info, &state->encoder);
	lodepng_free(converted);
  }
  else preProcessScanlines(&data, &datasize, image, w, h, &info, &state->encoder);

  ucvector_init(&outv);
  while(!state->error) /*while only executed once, to break on error*/
  {
#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
	size_t i;
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/
	/*write signature and chunks*/
	writeSignature(&outv);
	/*IHDR*/
	addChunk_IHDR(&outv, w, h, info.color.colortype, info.color.bitdepth, info.interlace_method);
#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
	/*unknown chunks between IHDR and PLTE*/
	if(info.unknown_chunks_data[0])
	{
	  state->error = addUnknownChunks(&outv, info.unknown_chunks_data[0], info.unknown_chunks_size[0]);
	  if(state->error) break;
	}
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/
	/*PLTE*/
	if(info.color.colortype == LCT_PALETTE)
	{
	  addChunk_PLTE(&outv, &info.color);
	}
	if(state->encoder.force_palette && (info.color.colortype == LCT_RGB || info.color.colortype == LCT_RGBA))
	{
	  addChunk_PLTE(&outv, &info.color);
	}
	/*tRNS*/
	if(info.color.colortype == LCT_PALETTE && getPaletteTranslucency(info.color.palette, info.color.palettesize) != 0)
	{
	  addChunk_tRNS(&outv, &info.color);
	}
	if((info.color.colortype == LCT_GREY || info.color.colortype == LCT_RGB) && info.color.key_defined)
	{
	  addChunk_tRNS(&outv, &info.color);
	}
#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
	/*bKGD (must come between PLTE and the IDAt chunks*/
	if(info.background_defined) addChunk_bKGD(&outv, &info);
	/*pHYs (must come before the IDAT chunks)*/
	if(info.phys_defined) addChunk_pHYs(&outv, &info);

	/*unknown chunks between PLTE and IDAT*/
	if(info.unknown_chunks_data[1])
	{
	  state->error = addUnknownChunks(&outv, info.unknown_chunks_data[1], info.unknown_chunks_size[1]);
	  if(state->error) break;
	}
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/
	/*IDAT (multiple IDAT chunks must be consecutive)*/
	state->error = addChunk_IDAT(&outv, data, datasize, &state->encoder.zlibsettings);
	if(state->error) break;
#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
	/*tIME*/
	if(info.time_defined) addChunk_tIME(&outv, &info.time);
	/*tEXt and/or zTXt*/
	for(i = 0; i != info.text_num; ++i)
	{
	  if(strlen(info.text_keys[i]) > 79)
	  {
		state->error = 66; /*text chunk too large*/
		break;
	  }
	  if(strlen(info.text_keys[i]) < 1)
	  {
		state->error = 67; /*text chunk too small*/
		break;
	  }
	  if(state->encoder.text_compression)
	  {
		addChunk_zTXt(&outv, info.text_keys[i], info.text_strings[i], &state->encoder.zlibsettings);
	  }
	  else
	  {
		addChunk_tEXt(&outv, info.text_keys[i], info.text_strings[i]);
	  }
	}
	/*LodePNG version id in text chunk*/
	if(state->encoder.add_id)
	{
	  unsigned alread_added_id_text = 0;
	  for(i = 0; i != info.text_num; ++i)
	  {
		if(!strcmp(info.text_keys[i], "LodePNG"))
		{
		  alread_added_id_text = 1;
		  break;
		}
	  }
	  if(alread_added_id_text == 0)
	  {
		addChunk_tEXt(&outv, "LodePNG", LODEPNG_VERSION_STRING); /*it's shorter as tEXt than as zTXt chunk*/
	  }
	}
	/*iTXt*/
	for(i = 0; i != info.itext_num; ++i)
	{
	  if(strlen(info.itext_keys[i]) > 79)
	  {
		state->error = 66; /*text chunk too large*/
		break;
	  }
	  if(strlen(info.itext_keys[i]) < 1)
	  {
		state->error = 67; /*text chunk too small*/
		break;
	  }
	  addChunk_iTXt(&outv, state->encoder.text_compression,
					info.itext_keys[i], info.itext_langtags[i], info.itext_transkeys[i], info.itext_strings[i],
					&state->encoder.zlibsettings);
	}

	/*unknown chunks between IDAT and IEND*/
	if(info.unknown_chunks_data[2])
	{
	  state->error = addUnknownChunks(&outv, info.unknown_chunks_data[2], info.unknown_chunks_size[2]);
	  if(state->error) break;
	}
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/
	addChunk_IEND(&outv);

	break; /*this isn't really a while loop; no error happened so break out now!*/
  }

  lodepng_info_cleanup(&info);
  lodepng_free(data);
  /*instead of cleaning the vector up, give it to the output*/
  *out = outv.data;
  *outsize = outv.size;

  return state->error;
}

unsigned lodepng_encode_memory(unsigned char** out, size_t* outsize, const unsigned char* image,
							   unsigned w, unsigned h, LodePNGColorType colortype, unsigned bitdepth)
{
  unsigned error;
  LodePNGState state;
  lodepng_state_init(&state);
  state.info_raw.colortype = colortype;
  state.info_raw.bitdepth = bitdepth;
  state.info_png.color.colortype = colortype;
  state.info_png.color.bitdepth = bitdepth;
  lodepng_encode(out, outsize, image, w, h, &state);
  error = state.error;
  lodepng_state_cleanup(&state);
  return error;
}

unsigned lodepng_encode_memory_std(unsigned char** out, size_t* outsize, const unsigned char* image,
							   unsigned w, unsigned h, unsigned colortype, unsigned bitdepth)
{
  return lodepng_encode_memory(out,outsize,image,w,h,(LodePNGColorType)colortype,bitdepth);
}

unsigned lodepng_encode32(unsigned char** out, size_t* outsize, const unsigned char* image, unsigned w, unsigned h)
{
  return lodepng_encode_memory(out, outsize, image, w, h, LCT_RGBA, 8);
}

unsigned lodepng_encode24(unsigned char** out, size_t* outsize, const unsigned char* image, unsigned w, unsigned h)
{
  return lodepng_encode_memory(out, outsize, image, w, h, LCT_RGB, 8);
}

#ifdef LODEPNG_COMPILE_DISK
unsigned lodepng_encode_file(const char* filename, const unsigned char* image, unsigned w, unsigned h,
							 LodePNGColorType colortype, unsigned bitdepth)
{
  unsigned char* buffer;
  size_t buffersize;
  unsigned error = lodepng_encode_memory(&buffer, &buffersize, image, w, h, colortype, bitdepth);
  if(!error) error = lodepng_save_file(buffer, buffersize, filename);
  lodepng_free(buffer);
  return error;
}

unsigned lodepng_encode32_file(const char* filename, const unsigned char* image, unsigned w, unsigned h)
{
  return lodepng_encode_file(filename, image, w, h, LCT_RGBA, 8);
}

unsigned lodepng_encode24_file(const char* filename, const unsigned char* image, unsigned w, unsigned h)
{
  return lodepng_encode_file(filename, image, w, h, LCT_RGB, 8);
}
#endif /*LODEPNG_COMPILE_DISK*/

void lodepng_encoder_settings_init(LodePNGEncoderSettings* settings)
{
  lodepng_compress_settings_init(&settings->zlibsettings);
  settings->filter_palette_zero = 1;
  settings->filter_strategy = LFS_MINSUM;
  settings->auto_convert = 1;
  settings->force_palette = 0;
  settings->predefined_filters = 0;
#ifdef LODEPNG_COMPILE_ANCILLARY_CHUNKS
  settings->add_id = 0;
  settings->text_compression = 1;
#endif /*LODEPNG_COMPILE_ANCILLARY_CHUNKS*/
}

#endif /*LODEPNG_COMPILE_ENCODER*/
#endif /*LODEPNG_COMPILE_PNG*/

#ifdef LODEPNG_COMPILE_ERROR_TEXT
/*
This returns the description of a numerical error code in English. This is also
the documentation of all the error codes.
*/
const char* lodepng_error_text(unsigned code)
{
  switch(code)
  {
	case 0: return "no error, everything went ok";
	case 1: return "nothing done yet"; /*the Encoder/Decoder has done nothing yet, error checking makes no sense yet*/
	case 10: return "end of input memory reached without huffman end code"; /*while huffman decoding*/
	case 11: return "error in code tree made it jump outside of huffman tree"; /*while huffman decoding*/
	case 13: return "problem while processing dynamic deflate block";
	case 14: return "problem while processing dynamic deflate block";
	case 15: return "problem while processing dynamic deflate block";
	case 16: return "unexisting code while processing dynamic deflate block";
	case 17: return "end of out buffer memory reached while inflating";
	case 18: return "invalid distance code while inflating";
	case 19: return "end of out buffer memory reached while inflating";
	case 20: return "invalid deflate block BTYPE encountered while decoding";
	case 21: return "NLEN is not ones complement of LEN in a deflate block";
	 /*end of out buffer memory reached while inflating:
	 This can happen if the inflated deflate data is longer than the amount of bytes required to fill up
	 all the pixels of the image, given the color depth and image dimensions. Something that doesn't
	 happen in a normal, well encoded, PNG image.*/
	case 22: return "end of out buffer memory reached while inflating";
	case 23: return "end of in buffer memory reached while inflating";
	case 24: return "invalid FCHECK in zlib header";
	case 25: return "invalid compression method in zlib header";
	case 26: return "FDICT encountered in zlib header while it's not used for PNG";
	case 27: return "PNG file is smaller than a PNG header";
	/*Checks the magic file header, the first 8 bytes of the PNG file*/
	case 28: return "incorrect PNG signature, it's no PNG or corrupted";
	case 29: return "first chunk is not the header chunk";
	case 30: return "chunk length too large, chunk broken off at end of file";
	case 31: return "illegal PNG color type or bpp";
	case 32: return "illegal PNG compression method";
	case 33: return "illegal PNG filter method";
	case 34: return "illegal PNG interlace method";
	case 35: return "chunk length of a chunk is too large or the chunk too small";
	case 36: return "illegal PNG filter type encountered";
	case 37: return "illegal bit depth for this color type given";
	case 38: return "the palette is too big"; /*more than 256 colors*/
	case 39: return "more palette alpha values given in tRNS chunk than there are colors in the palette";
	case 40: return "tRNS chunk has wrong size for greyscale image";
	case 41: return "tRNS chunk has wrong size for RGB image";
	case 42: return "tRNS chunk appeared while it was not allowed for this color type";
	case 43: return "bKGD chunk has wrong size for palette image";
	case 44: return "bKGD chunk has wrong size for greyscale image";
	case 45: return "bKGD chunk has wrong size for RGB image";
	/*the input data is empty, maybe a PNG file doesn't exist or is in the wrong path*/
	case 48: return "empty input or file doesn't exist";
	case 49: return "jumped past memory while generating dynamic huffman tree";
	case 50: return "jumped past memory while generating dynamic huffman tree";
	case 51: return "jumped past memory while inflating huffman block";
	case 52: return "jumped past memory while inflating";
	case 53: return "size of zlib data too small";
	case 54: return "repeat symbol in tree while there was no value symbol yet";
	/*jumped past tree while generating huffman tree, this could be when the
	tree will have more leaves than symbols after generating it out of the
	given lenghts. They call this an oversubscribed dynamic bit lengths tree in zlib.*/
	case 55: return "jumped past tree while generating huffman tree";
	case 56: return "given output image colortype or bitdepth not supported for color conversion";
	case 57: return "invalid CRC encountered (checking CRC can be disabled)";
	case 58: return "invalid ADLER32 encountered (checking ADLER32 can be disabled)";
	case 59: return "requested color conversion not supported";
	case 60: return "invalid window size given in the settings of the encoder (must be 0-32768)";
	case 61: return "invalid BTYPE given in the settings of the encoder (only 0, 1 and 2 are allowed)";
	/*LodePNG leaves the choice of RGB to greyscale conversion formula to the user.*/
	case 62: return "conversion from color to greyscale not supported";
	case 63: return "length of a chunk too long, max allowed for PNG is 2147483647 bytes per chunk"; /*(2^31-1)*/
	/*this would result in the inability of a deflated block to ever contain an end code. It must be at least 1.*/
	case 64: return "the length of the END symbol 256 in the Huffman tree is 0";
	case 66: return "the length of a text chunk keyword given to the encoder is longer than the maximum of 79 bytes";
	case 67: return "the length of a text chunk keyword given to the encoder is smaller than the minimum of 1 byte";
	case 68: return "tried to encode a PLTE chunk with a palette that has less than 1 or more than 256 colors";
	case 69: return "unknown chunk type with 'critical' flag encountered by the decoder";
	case 71: return "unexisting interlace mode given to encoder (must be 0 or 1)";
	case 72: return "while decoding, unexisting compression method encountering in zTXt or iTXt chunk (it must be 0)";
	case 73: return "invalid tIME chunk size";
	case 74: return "invalid pHYs chunk size";
	/*length could be wrong, or data chopped off*/
	case 75: return "no null termination char found while decoding text chunk";
	case 76: return "iTXt chunk too short to contain required bytes";
	case 77: return "integer overflow in buffer size";
	case 78: return "failed to open file for reading"; /*file doesn't exist or couldn't be opened for reading*/
	case 79: return "failed to open file for writing";
	case 80: return "tried creating a tree of 0 symbols";
	case 81: return "lazy matching at pos 0 is impossible";
	case 82: return "color conversion to palette requested while a color isn't in palette";
	case 83: return "memory allocation failed";
	case 84: return "given image too small to contain all pixels to be encoded";
	case 86: return "impossible offset in lz77 encoding (internal bug)";
	case 87: return "must provide custom zlib function pointer if LODEPNG_COMPILE_ZLIB is not defined";
	case 88: return "invalid filter strategy given for LodePNGEncoderSettings.filter_strategy";
	case 89: return "text chunk keyword too short or long: must have size 1-79";
	/*the windowsize in the LodePNGCompressSettings. Requiring POT(==> & instead of %) makes encoding 12% faster.*/
	case 90: return "windowsize must be a power of two";
	case 91: return "invalid decompressed idat size";
	case 92: return "too many pixels, not supported";
	case 93: return "zero width or height is invalid";
  }
  return "unknown error code";
}
#endif /*LODEPNG_COMPILE_ERROR_TEXT*/

