   namespace utils
   {
	  uint32 compute_max_mips(uint32 width, uint32 height)
	  {
		 if ((width | height) == 0)
			return 0;

		 uint32 num_mips = 1;

		 while ((width > 1U) || (height > 1U))
		 {
			width >>= 1U;
			height >>= 1U;
			num_mips++;
		 }

		 return num_mips;
	  }

   } // namespace utils

