   static void write_array_to_file(const char* pFilename, const vector<uint8>& buf)
   {
	  FILE* pFile = fopen(pFilename, "wb");
	  fwrite(&buf[0], buf.size(), 1, pFile);
	  fclose(pFile);
   }
#endif

