	void Finish();

	uint8_t* m_data;
	v2i m_size;
	size_t m_dataOffset;
private:

	BlockBitmapPtr m_bmp;
	bool m_done;
	std::vector<std::future<void>> m_work;
	std::mutex m_lock;
