static uint Average1( const uint8_t* data )
{
	uint32_t a = 0;
	for( int i=0; i<8; i++ )
	{
		a += *data++;
	}
	return a / 8;
}

static void CalcErrorBlock( const uint8_t* data, uint err[2] )
{
	for( int i=0; i<8; i++ )
	{
		uint v = *data++;
		err[0] += v;
		err[1] += v*v;
	}
}

static uint CalcError( const uint block[2], uint average )
{
	uint err = block[1];
	err -= block[0] * 2 * average;
	err += 8 * sq( average );
	return err;
}

static void ProcessAverages( uint* a )
{
	for( int i=0; i<2; i++ )
	{
		int c1 = mul8bit( a[i*2+1], 31 );
		int c2 = mul8bit( a[i*2], 31 );

		int diff = c2 - c1;
		if( diff > 3 ) diff = 3;
		else if( diff < -4 ) diff = -4;

		int co = c1 + diff;

		a[5+i*2] = ( c1 << 3 ) | ( c1 >> 2 );
		a[4+i*2] = ( co << 3 ) | ( co >> 2 );
	}
	for( int i=0; i<4; i++ )
	{
		a[i] = g_avg2[mul8bit( a[i], 15 )];
	}
}

static void EncodeAverages( uint64_t& _d, const uint* a, size_t idx )
{
	auto d = _d;
	d |= ( idx << 24 );
	size_t base = idx << 1;

	uint v;
	if( ( idx & 0x2 ) == 0 )
	{
		v = ( a[base+0] >> 4 ) | ( a[base+1] & 0xF0 );
	}
	else
	{
		v = a[base+1] & 0xF8;
		int32_t c = ( ( a[base+0] & 0xF8 ) - ( a[base+1] & 0xF8 ) ) >> 3;
		v |= c & ~0xFFFFFFF8;
	}
	d |= v | ( v << 8 ) | ( v << 16 );
	_d = d;
}

uint64_t ProcessAlpha( const uint8_t* src )
{
	uint64_t d = 0;

	{
		bool solid = true;
		const uint8_t* ptr = src + 1;
		for( int i=1; i<16; i++ )
		{
			if( *src != *ptr++ )
			{
				solid = false;
				break;
			}
		}
		if( solid )
		{
			uint c = *src & 0xF8;
			d |= 0x02000000 | ( c << 16 ) | ( c << 8 ) | c;
			return d;
		}
	}

	uint8_t b23[2][8];
	const uint8_t* b[4] = { src+8, src, b23[0], b23[1] };

	for( int i=0; i<4; i++ )
	{
		*(b23[1]+i*2) = *(src+i*4);
		*(b23[0]+i*2) = *(src+i*4+3);
	}

	uint a[8];
	for( int i=0; i<4; i++ )
	{
		a[i] = Average1( b[i] );
	}
	ProcessAverages( a );

	uint err[4] = {};
	for( int i=0; i<4; i++ )
	{
		uint errblock[2] = {};
		CalcErrorBlock( b[i], errblock );
		err[i/2] += CalcError( errblock, a[i] );
		err[2+i/2] += CalcError( errblock, a[i+4] );
	}
	size_t idx = GetLeastError( err, 4 );

	EncodeAverages( d, a, idx );

	uint terr[2][8] = {};
	uint tsel[16][8];
	auto id = g_id[idx];
	const uint8_t* data = src;
	for( size_t i=0; i<16; i++ )
	{
		uint* sel = tsel[i];
		uint bid = id[i];
		uint* ter = terr[bid%2];

		uint8_t c = *data++;
		int32_t pix = a[bid] - c;

		for( int t=0; t<8; t++ )
		{
			const int32_t* tab = g_table[t];
			uint idx = 0;
			uint err = sq( tab[0] + pix );
			for( int j=1; j<4; j++ )
			{
				uint local = sq( tab[j] + pix );
				if( local < err )
				{
					err = local;
					idx = j;
				}
			}
			*sel++ = idx;
			*ter++ += err;
		}
	}

	return FixByteOrder( EncodeSelectors( d, terr, tsel, id ) );
}

