	  width = other.width;
	  height = other.height;
	  minval = other.minval;
	  maxval = other.maxval;
	  num = other.num;
	  scale = other.scale;
#ifdef SUPPORT_HDR
	  depth = other.depth;
#endif
	  palette = other.palette;
	  frame_delay = other.frame_delay;
