static const uint16_t MANT_CHANCES[] = {1900, 1850, 1800, 1750, 1650, 1600, 1600, 2048, 2048,
										2048, 2048, 2048, 2048, 2048, 2048, 2048, 2048, 2048};
static const uint16_t ZERO_CHANCE = 1000;
static const uint16_t SIGN_CHANCE = 2048;
